package com.xworkz.core_java.Assignments.int_to_char;

public class IntToCharEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a =1;
			char c = (char)(a+'0');// here ascii value of '0' is 48, by adding (a+'0')=(1+48)=49, which ascii value of character 1
			System.out.println(c);// prints 1
	}

}
