package com.xworkz.core_java.Assignments.int_to_char;

public class IntToCharEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a ='1'; //which store ascii value of a ('1')
			char c = (char)a; // which converts ascii to character
			System.out.println(a); // 49
			System.out.println(c); // 1
	}

}
